let buttons=document.querySelectorAll(".btnplus"),modals=document.querySelectorAll(".custommodal"),closebtns=document.querySelectorAll(".close");const modalEvents=()=>{buttons.forEach((e,t)=>{e.addEventListener("click",()=>{modals[t].classList.add("show"),setTimeout(()=>{modals[t].style.opacity=1},100)})}),closebtns.forEach((e,t)=>{e.addEventListener("click",()=>{modals[t].style.opacity=0,setTimeout(()=>{modals[t].classList.remove("show")},300)})})},getCats=()=>{let e=document.querySelector(".catpics");for(let t=0;t<25;t++)fetch("https://aws.random.cat/meow").then(e=>e.json()).then(t=>{e.innerHTML+=`<a href="${t.file}" target="_blank"><img src="${t.file}"/></a>`})},getDogs=()=>{let e=document.querySelector(".dogpics");for(let t=0;t<25;t++)fetch("https://random.dog/woof.json?include=jpg,jpeg,gif,png").then(e=>e.json()).then(t=>{e.innerHTML+=`<a href="${t.url}" target="_blank"><img src="${t.url}"/></a>`})},getFoxes=()=>{let e=document.querySelector(".foxpics");for(let t=0;t<25;t++)fetch("https://randomfox.ca/floof/").then(e=>e.json()).then(t=>{e.innerHTML+=`<a href="${t.image}" target="_blank"><img src="${t.image}"/></a>`})};buttons.forEach((e,t)=>{e.addEventListener("click",()=>{modals[t].classList.add("show"),setTimeout(()=>{modals[t].style.opacity=1},100)})}),closebtns.forEach((e,t)=>{e.addEventListener("click",()=>{modals[t].style.opacity=0,setTimeout(()=>{modals[t].classList.remove("show")},300)})}),getCats(),getDogs(),getFoxes();